    AND  update_by = #{updateBy,jdbcType=VARCHAR}


    AND create_time >= #{beginTime}


    AND create_time <DATE_ADD(#{endTime}, INTERVAL 1 DAY)


    AND create_time >= #{beginTime} AND create_time < DATE_ADD(#{endTime}, INTERVAL 1 DAY)

