values (#{id,jdbcType=INTEGER}, #{idCard,jdbcType=VARCHAR}, #{orderNum,jdbcType=VARCHAR}, 
  #{data,jdbcType=VARCHAR}, #{errorMessage,jdbcType=VARCHAR}, #{type,jdbcType=CHAR}, 
  #{result,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, #{updateTime,jdbcType=VARCHAR}
