mapper.select:
select

from loan_loan_application
where 1=1

    AND  merch_id = #{merchId,jdbcType=INTEGER}


  AND  order_no = #{orderNo,jdbcType=VARCHAR}


    AND  borrower_id = #{borrowerId,jdbcType=VARCHAR}


    AND  borrower_name LIKE CONCAT('%', #{borrowerName},'%')


    AND borrower_phone = #{borrowerPhone,jdbcType=VARCHAR}


    AND borrower_id_card = #{borrowerIdCard,jdbcType=VARCHAR}


    AND loan_money = #{loanMoney,jdbcType=DECIMAL}


    AND stage_type = #{stageType,jdbcType=VARCHAR}


    AND rate_id = #{rateId,jdbcType=INTEGER}


    AND stage_limit = #{stageLimit,jdbcType=INTEGER}


    AND  template_id = #{templateId,jdbcType=INTEGER}


    AND submit_type = #{submitType,jdbcType=VARCHAR}


    AND submit_by_type = #{submitByType,jdbcType=VARCHAR}


    AND submit_by = #{submitBy,jdbcType=INTEGER}


    AND status = #{status,jdbcType=VARCHAR}


    AND status != #{excludeStatus,jdbcType=INTEGER}


    AND audit_type = #{auditType,jdbcType=VARCHAR}


    AND create_by = #{createBy,jdbcType=INTEGER}


    AND create_time >= #{beginTime}


    AND create_time <DATE_ADD(#{endTime}, INTERVAL 1 DAY)


    AND create_time >= #{beginTime} AND create_time < DATE_ADD(#{endTime}, INTERVAL 1 DAY)


    AND update_by = #{updateBy,jdbcType=INTEGER}

ORDER BY create_time DESC

select

from loan_loan_application
WHERE 1=1

    AND  merch_id = #{merchId,jdbcType=INTEGER}


    AND  order_no = #{orderNo,jdbcType=VARCHAR}


    AND  borrower_id = #{borrowerId,jdbcType=VARCHAR}


    AND  borrower_name LIKE CONCAT('%', #{borrowerName},'%')


    AND borrower_phone = #{borrowerPhone,jdbcType=VARCHAR}


    AND borrower_id_card = #{borrowerIdCard,jdbcType=VARCHAR}


    AND audit_type = #{auditType,jdbcType=VARCHAR}


    AND create_time >= #{beginTime}


    AND create_time <DATE_ADD(#{endTime}, INTERVAL 1 DAY)


    AND create_time >= #{beginTime} AND create_time < DATE_ADD(#{endTime}, INTERVAL 1 DAY)


 AND   status in

          #{s}


ORDER BY

    FIELD(status, 
                     #{ss}
                  ),
 create_time DESC
