mapper.select:
select

from loan_merchant_recharge
where 1=1

    AND  recharge_no = #{rechargeNo,jdbcType=VARCHAR}


 AND   merch_id = #{merchId,jdbcType=INTEGER}


    AND  count = #{count,jdbcType=INTEGER}


    AND status = #{status,jdbcType=VARCHAR}


    AND recharge_type = #{rechargeType,jdbcType=VARCHAR}


    AND  recharge_by = #{rechargeBy,jdbcType=INTEGER}


    AND recharge_time >= #{startTime}


    AND recharge_time <=DATE_ADD(#{endTime}, INTERVAL 1 DAY)


    AND recharge_time >= #{startTime} AND recharge_time <= DATE_ADD(#{endTime}, INTERVAL 1 DAY)

ORDER BY recharge_time DESC
