mapper.insert:

insert into loan_merchantinfo_copy (toid, merchant_name, link_man, 
  link_mobile, address, industry, 
  scale, certificate_name, certificate_url, 
  status, createtime, updatetime
  )
values (#{toid,jdbcType=VARCHAR}, #{merchantName,jdbcType=VARCHAR}, #{linkMan,jdbcType=VARCHAR}, 
  #{linkMobile,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{industry,jdbcType=VARCHAR}, 
  #{scale,jdbcType=VARCHAR}, #{certificateName,jdbcType=VARCHAR}, #{certificateUrl,jdbcType=VARCHAR}, 
  #{status,jdbcType=CHAR}, #{createtime,jdbcType=VARCHAR}, #{updatetime,jdbcType=VARCHAR}
  )

mapper.select:
select

from loan_merchantinfo_copy
where 1=1

  AND toid = #{toid,jdbcType=VARCHAR}


  AND merchant_name = #{merchantName,jdbcType=VARCHAR}


  AND link_man = #{linkMan,jdbcType=VARCHAR}


  AND link_mobile = #{linkMobile,jdbcType=VARCHAR}


  AND address = #{address,jdbcType=VARCHAR}


  AND industry = #{industry,jdbcType=VARCHAR}


  AND scale = #{scale,jdbcType=VARCHAR}


  AND certificate_name = #{certificateName,jdbcType=VARCHAR}


  AND certificate_url = #{certificateUrl,jdbcType=VARCHAR}


  AND status = #{status,jdbcType=CHAR}


  AND createtime = #{createtime,jdbcType=VARCHAR}


  AND updatetime = #{updatetime,jdbcType=VARCHAR}
