mapper.select:
select

from loan_order_copy
where 1=1

  AND toid = #{toid,jdbcType=VARCHAR}


  AND realname = #{realname,jdbcType=VARCHAR}


  AND id_card = #{idCard,jdbcType=VARCHAR}


  AND phone = #{phone,jdbcType=VARCHAR}


  AND status = #{status,jdbcType=CHAR}


  AND order_no = #{orderNo,jdbcType=VARCHAR}


  AND token = #{token,jdbcType=VARCHAR}


  AND createtime = #{createtime,jdbcType=VARCHAR}


  AND updatetime = #{updatetime,jdbcType=VARCHAR}


  AND userid = #{userid,jdbcType=VARCHAR}


  AND proxy_user = #{proxyUser,jdbcType=VARCHAR}


  AND riskstatus = #{riskstatus,jdbcType=CHAR}


  AND checkstatus = #{checkstatus,jdbcType=CHAR}


  AND checktime = #{checktime,jdbcType=VARCHAR}


  AND risk_check_result = #{riskCheckResult,jdbcType=VARCHAR}


  AND merid = #{merid,jdbcType=VARCHAR}
