mapper.select:
SELECT
o.order_no orderNo,
o.realname realname,
u.sex sex,
o.phone phone,
DATE_FORMAT(
o.createtime,
'%Y年%m月%d日 %H:%i:%s'
) createtime,
o.`status` `status`,
o.id_card,
pu.username username,
o.is_old isOld,
o.mer_id merId
FROM
loan_order o
LEFT JOIN loan_user u ON o.user_id = u.id
LEFT JOIN loan_proxy_user pu ON o.proxy_user = pu.id
WHERE 1=1

    AND o.proxy_user = #{proxyUserId}


    AND o.mer_id = #{merchId}



    and o.status IN

        #{item}




        AND o.realname = #{condition}




        AND o.phone = #{condition}




        AND o.id_card = #{condition}




    AND o.createtime >= #{startTime}


    AND o.createtime <= #{endTime}

ORDER BY o.createtime DESC

select

from loan_order
where 1=1

    AND id = #{id,jdbcType=INTEGER}


    AND realname = #{realname,jdbcType=VARCHAR}


    AND id_card = #{idCard,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=CHAR}


