mapper.select:
select

from loan_order_user_info
where 1=1

  AND id = #{id,jdbcType=INTEGER}


  AND order_no = #{orderNo,jdbcType=VARCHAR}


  AND order_token = #{orderToken,jdbcType=VARCHAR}


  AND merch_id = #{merchId,jdbcType=INTEGER}


  AND realname = #{realname,jdbcType=VARCHAR}


  AND id_card = #{idCard,jdbcType=VARCHAR}


  AND phone = #{phone,jdbcType=VARCHAR}


  AND phone_password = #{phonePassword,jdbcType=VARCHAR}


  AND sex = #{sex,jdbcType=CHAR}


  AND status = #{status,jdbcType=CHAR}


  AND education_account = #{educationAccount,jdbcType=VARCHAR}


  AND education_password = #{educationPassword,jdbcType=VARCHAR}


  AND home_address = #{homeAddress,jdbcType=VARCHAR}


  AND home_address_province = #{homeAddressProvince,jdbcType=VARCHAR}


  AND home_address_city = #{homeAddressCity,jdbcType=VARCHAR}


  AND home_address_area = #{homeAddressArea,jdbcType=VARCHAR}


  AND permanent_address = #{permanentAddress,jdbcType=VARCHAR}


  AND client_ip = #{clientIp,jdbcType=VARCHAR}


  AND latitude = #{latitude,jdbcType=VARCHAR}


  AND longitude = #{longitude,jdbcType=VARCHAR}


  AND createtime = #{createtime,jdbcType=TIMESTAMP}


  AND updatetime = #{updatetime,jdbcType=TIMESTAMP}
