mapper.select:
select

from loan_proxy_user
where 1=1

      AND id = #{id,jdbcType=INTEGER}


      AND username  LIKE  CONCAT('%', #{username}, '%')


      AND email = #{email,jdbcType=VARCHAR}


      AND pwd = #{pwd,jdbcType=VARCHAR}


      AND phone = #{phone,jdbcType=VARCHAR}


      AND status = #{status,jdbcType=CHAR}


      AND createtime = #{createtime}


      AND last_login_time = #{lastLoginTime}


      AND merch_id = #{merchId,jdbcType=INTEGER}


      AND is_master = #{isMaster,jdbcType=CHAR}


      AND first_login = #{firstLogin,jdbcType=CHAR}

select

from loan_proxy_user
where 1=1

      AND id = #{id,jdbcType=INTEGER}


      AND username  LIKE  CONCAT('%', #{username}, '%')


      AND email = #{email,jdbcType=VARCHAR}


      AND pwd = #{pwd,jdbcType=VARCHAR}


      AND phone = #{phone,jdbcType=VARCHAR}


      AND status = #{status,jdbcType=CHAR}


      AND createtime = #{createtime}


      AND last_login_time = #{lastLoginTime}


      AND merch_id = #{merchId,jdbcType=INTEGER}


      AND is_master = #{isMaster,jdbcType=CHAR}


      AND first_login = #{firstLogin,jdbcType=CHAR}

select

from loan_proxy_user
where 1=1

    AND id = #{id,jdbcType=INTEGER}


    AND username  LIKE  CONCAT('%', #{username}, '%')


    AND email = #{email,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=CHAR}


    AND createtime = #{createtime}


    AND last_login_time = #{lastLoginTime}


    AND merch_id = #{merchId,jdbcType=INTEGER}


    AND is_master = #{isMaster,jdbcType=CHAR}


    AND first_login = #{firstLogin,jdbcType=CHAR}

ORDER  BY last_login_time DESC ,createtime DESC

mapper.update:
update loan_proxy_user
set
status = #{status}
where
merch_id = #{merchId}

    AND is_master = #{type}

mapper.select:
SELECT pu.id id, pu.username username, pu.pwd, pu.email email, pu.phone phone, pu.`status` `status`, pu.first_login firstLogin, DATE_FORMAT( pu.createtime, '%Y-%m-%d %T' ) createtime, is_master isMaster, DATE_FORMAT( pu.last_login_time, '%Y-%m-%d %T' ) lastLoginTime, r.role_name roleName, pu.merch_id merchId, m.merchant_name merchantName, m.short_name merchantShortName, m.en_name merchantEnName FROM `loan_proxy_user` pu LEFT JOIN loan_user_role_webapp ur ON ur.user_id = pu.id LEFT JOIN loan_role_webapp r ON r.id = ur.role_id LEFT JOIN loan_merchant_info m ON m.id = pu.merch_id WHERE pu.is_master=0
AND pu.merch_id = #{merchId}
AND pu.username LIKE CONCAT('%',#{condition},'%')
AND m.merchant_name LIKE CONCAT('%',#{condition},'%')
AND pu.email = #{condition}
AND pu.phone = #{condition}
AND pu.createtime >= #{startTime}
AND pu.createtime <= #{endTime}
ORDER BY pu.createtime DESC
