mapper.select:
select

from loan_rate_template
where 1=1

   AND rate_name = #{rateName,jdbcType=VARCHAR}


    AND  staging_type = #{stagingType,jdbcType=INTEGER}


    AND  interest_rate = #{interestRate,jdbcType=DECIMAL}


    AND service_charge = #{serviceCharge,jdbcType=DECIMAL}


    AND deposit_type = #{depositType,jdbcType=INTEGER}


    AND  deposit_money = #{depositMoney,jdbcType=DECIMAL}


    AND overdue_type = #{overdueType,jdbcType=INTEGER}


    AND overdue_money_type = #{overdueMoneyType,jdbcType=INTEGER}


    AND  overdue_rate = #{overdueRate,jdbcType=DECIMAL}


    AND  mer_id = #{merId,jdbcType=INTEGER}
