mapper.select:
select

from loan_repayment_serial_record
where 1=1

  AND  merch_id = #{merchId,jdbcType=INTEGER}


    AND   borrower_id = #{borrowerId,jdbcType=INTEGER}


    AND deal_serial_no = #{dealSerialNo,jdbcType=VARCHAR}


    AND  accounting_serial_no = #{accountingSerialNo,jdbcType=VARCHAR}


    AND transfer_account_name = #{transferAccountName,jdbcType=VARCHAR}


    AND  status = #{status,jdbcType=VARCHAR}


    AND source = #{source,jdbcType=VARCHAR}


    AND update_source = #{updateSource,jdbcType=VARCHAR}


    AND borrower_id IN 
                      #{bId}


ORDER BY deal_time

select
status,COUNT(1) count
from loan_repayment_serial_record
where 1=1

  AND  merch_id = #{merchId,jdbcType=INTEGER}


    AND   borrower_id = #{borrowerId,jdbcType=INTEGER}


    AND   last_match_type = #{lastMatchType,jdbcType=VARCHAR}


    AND deal_serial_no = #{dealSerialNo,jdbcType=VARCHAR}


    AND  accounting_serial_no = #{accountingSerialNo,jdbcType=VARCHAR}


    AND transfer_account_name = #{transferAccountName,jdbcType=VARCHAR}


    AND  status = #{status,jdbcType=VARCHAR}


    AND source = #{source,jdbcType=VARCHAR}


    AND update_source = #{updateSource,jdbcType=VARCHAR}


  AND borrower_id IN 
        #{bId}


  GROUP BY status
