mapper.select:
SELECT r.id id, r.res_name resName, r.res_url resUrl, r.res_remark resRemark, r.`status` `status`, r.type type, r.parent_id parentId, r.sort sort, DATE_FORMAT( r.updatetime, '%Y年%m月%d日 %H:%i:%s' ) updatetime, CASE WHEN ur.id IS NULL THEN FALSE ELSE TRUE END AS `select` FROM `loan_resources_webapp` r LEFT JOIN loan_role_resources_webapp rr ON r.id = rr.resources_id LEFT JOIN loan_user_role_webapp ur ON ur.role_id = rr.role_id AND ur.user_id = #{userId}
WHERE r.type = #{type}
GROUP BY r.id ORDER BY sort ASC

SELECT r.id id, r.res_name resName, r.res_url resUrl, r.res_remark resRemark, r.`status` `status`, r.type type, r.parent_id parentId, r.sort sort, DATE_FORMAT( r.updatetime, '%Y年%m月%d日 %H:%i:%s' ) updatetime, CASE WHEN mr.id IS NULL THEN FALSE ELSE TRUE END AS `select` FROM `loan_resources_webapp` r LEFT JOIN loan_merch_resources_webapp mr ON mr.resources_id = r.id AND mr.merch_id = #{merchId}
WHERE r.type = #{type}
GROUP BY r.id ORDER BY sort ASC

SELECT r.id id, r.res_name resName, r.res_url resUrl, r.res_remark resRemark, r.`status` `status`, r.type type, r.parent_id parentId, r.sort sort, DATE_FORMAT( r.updatetime, '%Y年%m月%d日 %H:%i:%s' ) updatetime, CASE WHEN rr.id IS NULL THEN FALSE ELSE TRUE END AS `select` FROM `loan_resources_webapp` r LEFT JOIN loan_role_resources_webapp rr ON rr.resources_id = r.id AND role_id = #{roleId}
WHERE r.type = #{type}
GROUP BY r.id ORDER BY sort ASC

select

from loan_resources_webapp
where 1=1

  AND id = #{id,jdbcType=INTEGER}


  AND res_name = #{resName,jdbcType=VARCHAR}


  AND res_url = #{resUrl,jdbcType=VARCHAR}


  AND res_remark = #{resRemark,jdbcType=VARCHAR}


  AND status = #{status,jdbcType=CHAR}


  AND type = #{type,jdbcType=INTEGER}


  AND parent_id = #{parentId,jdbcType=INTEGER}


  AND sort = #{sort,jdbcType=INTEGER}


  AND updatetime = #{updatetime,jdbcType=TIMESTAMP}


  AND createtime = #{createtime,jdbcType=TIMESTAMP}

SELECT r.id id, r.res_name resName, r.res_url resUrl, r.res_remark resRemark, r.`status` status, r.type type, r.parent_id parentId, r.sort sort, rm.res_name parentName, DATE_FORMAT( r.updatetime, '%Y年%m月%d日 %H:%i:%s' ) updatetime FROM `loan_resources_webapp` r LEFT JOIN loan_resources_webapp rm ON r.parent_id = rm.id WHERE 1 = 1
AND r.type = #{type}
AND r.parent_id = #{parentId}
AND r.res_name = #{resName}
AND r.res_url = #{resUrl}
ORDER BY r.createtime DESC

SELECT r.id id, r.res_name resName, r.res_url resUrl, r.res_remark resRemark, r.`status` status, r.type type, r.parent_id parentId, r.sort sort, DATE_FORMAT( r.updatetime, '%Y年%m月%d日 %H:%i:%s' ) updatetime, CASE WHEN mr.id IS NULL THEN FALSE ELSE TRUE END AS `select` FROM `loan_resources_webapp` r LEFT OUTER JOIN loan_merch_resources_webapp mr ON mr.resources_id = r.id
AND mr.merch_id = #{merchId}
WHERE r.type = #{type}
GROUP BY r.id

SELECT r.id id, r.res_name resName, r.res_url resUrl, r.res_remark resRemark, r.`status` status, r.type type, r.parent_id parentId, r.sort sort, DATE_FORMAT( r.updatetime, '%Y年%m月%d日 %H:%i:%s' ) updatetime, CASE WHEN rr.id IS NULL THEN FALSE ELSE TRUE END AS `select` FROM `loan_resources_webapp` r LEFT JOIN loan_merch_resources_webapp mr ON mr.resources_id = r.id LEFT JOIN loan_role_resources_webapp rr ON r.id = rr.resources_id AND rr.merch_id = mr.merch_id
AND rr.role_id = #{roleId}
WHERE 1=1 AND mr.merch_id = #{merchId}
AND r.type = #{type}
GROUP BY r.id

SELECT r.* FROM loan_resources_webapp r INNER JOIN loan_role_resources_webapp rr ON rr.resources_id = r.id INNER JOIN loan_user_role_webapp ur ON ur.role_id = rr.role_id INNER JOIN loan_proxy_user pu ON pu.id = ur.user_id WHERE pu.id = #{userId}
AND r.type = #{type}
ORDER BY r.sort ASC

SELECT r.* FROM loan_resources_webapp r INNER JOIN loan_merch_resources_webapp mr ON mr.resources_id = r.id WHERE mr.merch_id = #{merchId}
AND r.type = #{type}
ORDER BY r.sort ASC
